import React from "react";
import TransactionItem from "./TransactionItem"

function Transactions({transactions}){
    return(
        <table className="ui celled striped padded table">
            <tbody>
            <tr>
               <th><h2 className="ui center aligned header">Date</h2></th>
               <th><h2 className="ui center aligned header">Description</h2></th>
               <th><h2 className="ui center aligned header">Category</h2></th>
               <th><h2 className="ui center aligned header">Amount</h2></th>
            </tr>
            {transactions.map((transaction) => {
                return  <TransactionItem
                        key={transaction.id}
                        date={transaction.date}
                        description={transaction.description}
                        category={transaction.category}
                        amount={transaction.amount}
                        />
            })
            }
            </tbody>
        </table>
    )
}

export default Transactions;
